import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as e,e as r}from"./app-3bfa5009.js";const o={},a=r('<h1 id="传承与创造-砖木雕的-技-与-艺-阴佳-核心-人文经典与审美素养-1-5学分-四平" tabindex="-1"><a class="header-anchor" href="#传承与创造-砖木雕的-技-与-艺-阴佳-核心-人文经典与审美素养-1-5学分-四平" aria-hidden="true">#</a> 传承与创造：砖木雕的“技”与“艺” 阴佳 核心 人文经典与审美素养 1.5学分 四平</h1><p><strong>part 1</strong></p><p>参考《同济大学通识教育2020学生手册》第45页。</p><p><strong>part 2</strong></p><p>考核形式：课堂训练作业，最终个人作品展示</p><p>个人评价：</p><p>公共选修课~</p><p>几位老师们都非常友好，课堂氛围超级活跃，最后的给分也很好！</p><p>课程内容是以形态创造为主线，对大家的艺术创造有一定的要求，但是因为是公共选修课，大家的专业背景都很复杂，所以老师们也是以鼓励启发式教育为主，大家可以放心开脑洞。</p><p>课程前期是进行造型基础训练；在期中左右，我们当时是去浙江东阳实践基地，和当地的木雕师傅们一起制作传统榫卯结构；下半学期的安排是最令人期待的成果制作：根据调研得到的灵感，每个人都要制作一个木雕作品，我们当时大概花了5周左右的时间从设计图案到制作最后的成果！</p><p>最后个人木雕作品出来的那一刻，我真的感觉非常值得！</p><p>尽管老师们每次的教学安排和成果要求会根据课程情况有所调整，但大体上还是差不多的，因此这篇推荐可以给大家做一定程度上的参考。</p><p>总而言之，这是一门非常宝藏的课程，大家一定要冲哇！</p>',13),_=[a];function n(s,c){return t(),e("div",null,_)}const h=p(o,[["render",n],["__file","传承与创造.html.vue"]]);export{h as default};
