import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as _,c as t,e}from"./app-3bfa5009.js";const r={},a=e('<h1 id="生命探秘-徐磊-精品-科学探索与生命关怀-1-5学分" tabindex="-1"><a class="header-anchor" href="#生命探秘-徐磊-精品-科学探索与生命关怀-1-5学分" aria-hidden="true">#</a> 生命探秘 徐磊 精品 科学探索与生命关怀 1.5学分</h1><p><strong>part 1</strong></p><p>参考《同济大学通识教育2020学生手册》第79页。</p><p><strong>part 2</strong></p><p>（线上情况）</p><p>课程介绍：中文。精品通识选修课。</p><p>从不同的角度为我们探秘生命的奥秘，邀请医学系不同方向的老师从不同角度为我们解读生命，上完课之后还觉得挺茅塞顿开的。</p><p>讲课特色：老师会抛砖引玉，引发同学们的思考。</p><p>考核方式：平时八次小作业，每次十分，最后二十分是期末小论文。只要按规定完成给分挺好的。</p><p><strong>part 3</strong></p><p>考核形式：期末交一篇小论文</p><p>个人评价：</p><p>课程挂在徐磊老师名下，但实际大概有十位老师轮流上课，每一两位老师讲一个主题的生物医学知识，内容如下：</p><p>1多姿多彩的微观世界</p><p>2和你看不到的小伙伴</p><p>3神奇的血液</p><p>4食物的消化旅程</p><p>5食物里的电波</p><p>6身体里的电波</p><p>7我的基因我知道</p><p>8食物营养知多少</p><p>9药物研发与药物滥用</p><p>个人感觉绝大多数内容很有趣，课程选择的主题都很有实用价值。</p><p>老师会要求下载蓝墨云班课的app，偶尔会用app点名，每个主题最后一次课会在app上布置几道选择题在课上最后几分钟完成，做不对也没关系不会影响课程成绩，期末交一篇小论文，给分还不错。</p>',24),o=[a];function n(s,c){return _(),t("div",null,o)}const h=p(r,[["render",n],["__file","生命探秘 徐磊 精品 科学探索与生命关怀 1.html.vue"]]);export{h as default};
